import Axios from "axios";
import React, { useEffect, useRef } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import Styled from 'styled-components';

const FormContainer = Styled.form`
    display: flex;
    align-items: center;
    gap: 20px;
    flex-wrap: wrap;
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px; 
    width: 1100px;
    justify-content: center;
`;

const InputArea = Styled.div`
    display: flex;
    flex-direction: column;
    width: 290px;
`;

const Input = Styled.input`
    width: 230px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const Select = Styled.select`
    width: 230px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const Label = Styled.label`
    font-size: 18px;
`;

const Button = Styled.button`
    width: 130px;
    padding: 10px;
    cursor: pointer;
    border-radius: 7px;
    border: none;
    background-color: #2c73d2;
    color: white;
    height: 42px;
`;

const Form = ({ onEdit }) => {
    const ref = useRef();

    useEffect(() => {
        if (onEdit) {

            const employee = ref.current;

            employee.employeeCpf.value = onEdit.employeeCpf;
            employee.employeeName.value = onEdit.employeeName;
            employee.employeeSector.value = onEdit.employeeSector;
        }
    }, [onEdit]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const employee = ref.current;

        if(
            !employee.employeeCpf.value ||
            !employee.employeeName.value ||
            !employee.employeeSector.value
        ) {
            return toast.warn("Preencha todos os campos!");
        }

        if (onEdit) {
            await Axios
            .put("http://localhost:8080" + onEdit.EmployeeId, {
                cpf: employee.employeeCpf.value,
                name: employee.employeeName.value,
                sector: employee.employeeSector.value,
            })
            .then(({ data }) => toast.success(data))
            .catch(({ data }) => toast.error(data));
        } else {
            await Axios
            .post("http://localhost:8080", {
                cpf: employee.employeeCpf.value,
                name: employee.employeeName.value,
                sector: employee.employeeSector.value,
            })
            .then(({ data }) => toast.success(data))
            .catch()
        }
    };

    return (
        <FormContainer ref={ref} onSubmit={handleSubmit}>
            <InputArea>
                <Label>CPF do Funcion√°rio</Label>
                <Input name='employeeCpf' type='text' placeholder='000.000.000-00' />
            </InputArea>
            <InputArea>
                <Label>Nome do funcion√°rio</Label>
                <Input name='employeeName' type='text' />
            </InputArea>
            <InputArea>
                <Label>Setor do Funcion√°rio</Label>
                <Select name="employeeSector">
                    <option value="">Selecione o Setor</option>
                    <option value="1">Desenvolvimento</option>
                    <option value="2">RH</option>
                    <option value="3">Infraestrutura</option>
                    <option value="4">Contabilidade</option>
                </Select>
            </InputArea>
            <Button type="submit">Cadastrar</Button>
        </FormContainer>
    );
};

export default Form;
