import React, { useRef } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import Styled from 'styled-components';

const FormContainer = Styled.form`
    display: flex;
    align-items: center;
    gap: 10px;
    flex-wrap: wrap;
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px; 
    width: 110px;
`;

const InputArea = Styled.div`
    display: flex;
    flex-direction: column;
    width: 230px;
    background-color: pink;
`;

const Input = Styled.input`
    width: 230px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const Select = Styled.select`
    width: 230px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const Label = Styled.label`
    font-size: 18px;
`;

const Button = Styled.button`
    width: 130px;
    padding: 10px;
    cursor: pointer;
    border-radius: 7px;
    border: none;
    background-color: #2c73d2;
    color: white;
    height: 42px;
`;

const Form = ({ onEdit }) => {
    const ref = useRef();

    return (
        <FormContainer>
            <h2>Cadastrar Funcion치rios</h2>
            <InputArea>
                <Label>CPF do Funcion치rio</Label>
                <Input name='employeeCpf' type='text' placeholder='000.000.000-00' />
            </InputArea>
            <InputArea>
                <Label>Nome do funcion치rio</Label>
                <Input name='employeeName' type='text' />
            </InputArea>
            <InputArea>
                <Label>Setor do Funcion치rio</Label>
                <Select name="employeeSector">
                    <option value="">Selecione o Setor</option>
                    <option value="1">Desenvolvimento</option>
                    <option value="2">RH</option>
                    <option value="3">Infraestrutura</option>
                    <option value="4">Contabilidade</option>
                </Select>
            </InputArea>
            <Button type="submit">Cadastrar</Button>
        </FormContainer>
    );
};

export default Form;
