import React, { useEffect, useRef } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import Styled from 'styled-components';

const FormContainer = Styled.form`
    display: flex;
    align-items: center;
    gap: 20px;
    flex-wrap: wrap;
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px; 
    width: 1100px;
    justify-content: center;
`;

const InputArea = Styled.div`
    display: flex;
    flex-direction: column;
    width: 290px;
`;

const Input = Styled.input`
    width: 230px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const Select = Styled.select`
    width: 230px;
    padding: 0 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;

const Label = Styled.label`
    font-size: 18px;
`;

const Button = Styled.button`
    width: 130px;
    padding: 10px;
    cursor: pointer;
    border-radius: 7px;
    border: none;
    background-color: #2c73d2;
    color: white;
    height: 42px;
`;

const Form = ({ onEdit }) => {
    const ref = useRef();

    useEffect(() => {
        if (onEdit) {

            const employee = ref.current;

            employee.employeeCpf.value = onEdit.employeeCpf;
            employee.name.value = onEdit.name;
            employee.sector.value = onEdit.sector;
        }
    }, [onEdit]);

    return (
        <FormContainer>
            <InputArea>
                <Label>CPF do Funcionário</Label>
                <Input name='employeeCpf' type='text' placeholder='000.000.000-00' />
            </InputArea>
            <InputArea>
                <Label>Nome do funcionário</Label>
                <Input name='employeeName' type='text' />
            </InputArea>
            <InputArea>
                <Label>Setor do Funcionário</Label>
                <Select name="employeeSector">
                    <option value="">Selecione o Setor</option>
                    <option value="1">Desenvolvimento</option>
                    <option value="2">RH</option>
                    <option value="3">Infraestrutura</option>
                    <option value="4">Contabilidade</option>
                </Select>
            </InputArea>
            <Button type="submit">Cadastrar</Button>
        </FormContainer>
    );
};

export default Form;
